<script>
document.addEventListener("DOMContentLoaded", () => {

    let data = JSON.parse(<?php echo $data; ?>);
    console.log(data)
    const regions = document.querySelectorAll(".cls-1");
    const tooltip = document.getElementById("tooltip");
    
    const regionsLinks = document.querySelectorAll('path[data-region-code]');
    console.log(regionsLinks)
    // Перебираем все ссылки
    regionsLinks.forEach((path) => {
        const regionCode = path.getAttribute('data-region-code');
        console.log(regionCode)  // Получаем код региона
        const selectedR = data.find((el) => el.code == regionCode);  // Ищем регион по коду
        console.log(regionCode)
        // Если регион найден, обновляем href
        if (selectedR) {
          const link = path.closest('a');
          const newHref = `/region/${selectedR.id}`;
          if (link){
            link.setAttribute('href', newHref);  // Обновляем атрибут href
          }  // Формируем новый href
        }
    });

    const windowWidth = window.screen.availWidth;
    console.log(windowWidth);

    regions.forEach((region) => {
      
      // regionCode.setAttribute('href', `/regiom/${selectedRegion.id}`)
      
      if (windowWidth < 768) {
        region.addEventListener("click", function (el) {
          el.preventDefault();
        });
      }
      
      region.addEventListener("mouseover", (e) => {
        // const regionName = region.getAttribute("data-region-name");
        const regionCode = region.getAttribute("data-region-code");
        const eventsCount = region.getAttribute("data-events-count"); // Количество мероприятий
        
        let selectedRegion = data.filter((el) => el.code == regionCode ? el : '' )[0];
        // console.log(selectedRegion)
        // Формируем содержимое всплывающего окна
        if (windowWidth < 768) {
            tooltip.innerHTML = `
            <p class="fs-20 title"> ${selectedRegion.name}</p>
            <p class="fs-16"><span class="fs-20">${selectedRegion.events_count} </span> Активных мероприятий </p>
            <a href="/region/${selectedRegion.id}" class="tooltip-link">Смотреть мероприятия</a>
            `;
        } else {
            tooltip.innerHTML = `
            <p class="fs-20 title"> ${selectedRegion.name}</p>
            <p class="fs-16"> <span class="fs-20">${selectedRegion.events_count} </span> Активных мероприятий </p>
            `;
        }

        tooltip.style.pointerEvents = "auto"; // Включаем обработку событий для ссылки
        tooltip.classList.add("show");
      });

        region.addEventListener("mousemove", (e) => {
        const tooltipWidth = tooltip.offsetWidth; // Получаем ширину всплывающего окна
        const positionX = e.pageX + 20;

        // Если курсор близко к правому краю экрана, показываем окно слева от курсора
        if (positionX + tooltipWidth > window.innerWidth) {
            tooltip.style.left = `${e.pageX - tooltipWidth - 10}px`; // Позиция слева от курсора
        } else {
            tooltip.style.left = `${positionX}px`; // Позиция справа от курсора
        }

        tooltip.style.top = `${e.pageY + 10}px`;
        });

        region.addEventListener("mouseout", () => {
        // Скрываем всплывающее окно только если мышь не находится над ним
        tooltip.classList.remove("show");
        });

        // Добавляем обработчик события на сам tooltip, чтобы не скрывать его, когда пользователь наводит мышь
        tooltip.addEventListener("mouseover", () => {
        tooltip.classList.add("show");
        });

        tooltip.addEventListener("mouseout", () => {
        tooltip.classList.remove("show");
        });
    });
});

</script>